USE BellaBeat; GO CREATE OR ALTER VIEW dbo.v_daily_user_summary AS SELECT a.Id, a.activity_date, a.TotalSteps, a.SedentaryMinutes, a.VeryActiveMinutes, a.Calories, s.TotalMinutesAsleep, s.TotalTimeInBed FROM dbo.v_daily_activity a LEFT JOIN dbo.v_sleep s ON a.Id = s.Id AND a.activity_date = s.sleep_date;